});

var username = req.moduleParams.username;
var User = calipso.db.model('User');

// Quickly check that the user is an admin or it is their account
if (req.session.user && (req.session.user.isAdmin || req.session.user.username === username)) {
  // We're ok
} else {
  req.flash('error', req.t('You are not authorised to perform that action.'));
  res.redirect('/');
  return;
}

// Get the password values and remove from form
// This ensures they are never stored
var new_password = form.user.new_password;
delete form.user.new_password;
var repeat_password = form.user.repeat_password;
delete form.user.repeat_password;
var old_password = form.user.old_password;
delete form.user.old_password;

User.findOne({username:username}, function (err, u) {
  if (err) {
    req.flash('error', req.t('Could not find user because {msg}.', {msg:err.message}));
    if (res.statusCode != 302) {
      res.redirect('/');
      return;
    }
    next();
    return;
  }

  u.fullname = form.user.fullname;
  u.username = uname || form.user.username;
  u.email = form.user.email;
  u.language = form.user.language;
  u.about = form.user.about;
  u.showName = form.user.showName;
  u.showEmail = form.user.showEmail;

  // Update user roles and admin flag
  if (req.session.user && req.session.user.isAdmin) {
    var newRoles = [];
    u.isAdmin = false; // TO-DO Replace
    for (var role in
      form.user.roleList) {
      if (form.user.roleList[role]) {
        newRoles.push(role);
      }
    }
    u.roles = newRoles;
  }

  // Check to see if we are changing the password
  if (old_password) {

    // Check to see if old password is valid
    calipso.lib.crypto.check(old_password, u.hash, function (err, ok) {
      if (u.hash != '' && !ok) {
        req.flash('error', req.t('Your old password was invalid.'));
        res.redirect('back');
        return;
      }

      // Check to see if new passwords match
      if (new_password != repeat_password) {
        req.flash('error', req.t('Your new passwords do not match.'));
        res.redirect('back');
        return;
      }

      // Check to see if new passwords are blank
      if (new_password === '') {
        req.flash('error', req.t('Your password cannot be blank.'));
        res.redirect('back');
        return;
      }

      u.password = ''; // Temporary for migration to hash, remove later
      // Create the hash
      calipso.lib.crypto.hash(new_password, calipso.config.get('session:secret'), function (err, hash) {
        if (err) {
          req.flash('error', req.t('Could not hash password because {msg}.', {msg:err.message}));
          if (res.statusCode != 302) {
            res.redirect('/');
            return;
          }
          next();
          return;
        }
        u.hash = hash;
        saveUser();
      });
    });
  } else {
    saveUser();
  }
  function saveUser() {
    calipso.e.pre_emit('USER_UPDATE', u);
    u.save(function (err) {
      if (err) {

        req.flash('error', req.t('Could not save user because {msg}.', {msg:err.message}));
        if (res.statusCode != 302) {
          // Redirect to old page
          res.redirect('/user/profile/' + encodeURIComponent(username) + '/edit');
          return;
        }

      } else {

        calipso.e.post_emit('USER_UPDATE', u);

        // Update session details if your account
        if (req.session.user && (req.session.user.username === username)) {
          // Allows for name change
          createUserSession(req, res, u, function (err) {
            if (err) {
              calipso.error("Error saving session: " + err);
            }
          });
        }

        // Redirect to new page
        res.redirect('/user/profile/' + encodeURIComponent(u.username));
        return;

      }
      // If not already redirecting, then redirect
      next();
    });
  }
});

var returnTo = req.moduleParams.returnto || null;
if (req.session && req.session.user) {

  var User = calipso.db.model('User');
  if (req.logout) {
    req.logout();
  }
  User.findOne({username:req.session.user.username}, function (err, u) {

    req.session.user = null;
    req.session.save(function (err) {
      // Check for error
      calipso.e.post_emit('USER_LOGOUT', u);
      if (res.statusCode != 302) {
        res.redirect(returnTo || 'back');
        return;
      }
      next();

    });

  });

} else {
  // Fail quietly
  res.redirect(returnTo || 'back');
}

var id = req.moduleParams.id;

Role.findById(id, function (err, c) {
  if (!err && c) {

    calipso.form.mapFields(form.role, c);
    c.ispublic = form.role.ispublic === "Yes" ? true : false;
    c.updated = new Date();

    calipso.e.pre_emit('USER_ROLE_UPDATE', c, function (c) {

      c.save(function (err) {
        if (err) {
          req.flash('error', req.t('Could not update role because {msg}.', {msg:err.message}));
          if (res.statusCode != 302) {
            // Don't redirect if we already are, multiple errors
            res.redirect('/user/role/edit/' + encodeURIComponent(id));
          }
          next();
        } else {
          calipso.e.post_emit('USER_ROLE_UPDATE', c, function (c) {
            res.redirect('/user/role/show/' + encodeURIComponent(id));
            next();
          });
        }
      });
    });
  } else {
    req.flash('error', req.t('Could not locate that role.'));
    res.redirect('/user/role');
    next();
  }
});

var contentId = req.moduleParams.id;
var id = req.moduleParams.version;
var format = req.moduleParams.format || 'html';

var Content = calipso.db.model('Content');
var ContentVersion = calipso.db.model('ContentVersion');

ContentVersion.findById(id, function (err, version) {

  if (err && !version) {
    calipso.err(err);
    next();
    return;
  }

  // Copy over
  Content.findById(contentId, function (err, content) {

    if (err && !content) {
      calipso.err(err)
      next();
      return;
    }

    calipso.utils.copyMongoObject(version, content, content.schema);

    content.author = req.session.user.username;
    content.set("comment", 'Reverted to version: ' + content.updated);
    content.updated = new Date();
    content.set("version", 'Yes');

    content.save(function (err) {
      res.redirect('/content/show/' + encodeURIComponent(contentId));
      next();
    });

  });

});

      var id = req.moduleParams.id;

      Asset.findById(id, function (err, c) {
        if (c) {
          calipso.lib.assets.checkPermission(c.alias, req.session.user.username, 'modify', function (err, allowed) {
            if (!allowed) {
              res.statusCode = 403;
              req.flash('error', req.t('You\'re not allowed to edit this asset.'));
              return next();
            }
            // Default mapper
            calipso.form.mapFields(form.asset, c);
            // Fields that are mapped specifically
            c.updated = new Date();
            c.updated_by = req.session.user.username;
//              c.alias = form.asset.alias ? form.asset.alias : titleAlias(c.title);
//              c.tags = form.asset.tags ? form.asset.tags.replace(/[\s]+/g, "").split(",") : [];

            // Emit pre event
            // This does not allow you to change the content
            calipso.e.pre_emit('ASSET_UPDATE', c, function (c) {

              c.save(function (err) {
                if (err) {

                  var errorMsg = '';
                  if (err.errors) {
                    for (var error in err.errors) {
                      errorMessage = error + " " + err.errors[error] + '\r\n';
                    }
                  } else {
                    errorMessage = err.message;
                  }
                  req.flash('error', req.t('Could not update content because {msg}', {msg:errorMessage}));
                  if (res.statusCode != 302) {
                    // Don't redirect if we already are, multiple errors
                    res.redirect('back');
                  }
                  next();

                } else {
                  calipso.lib.assets.updateParents(c.folder, req.session.user.username, function (err) {
                    req.flash('info', req.t('Content saved.'));

                    // Raise CONTENT_CREATE event
                    calipso.e.post_emit('CONTENT_UPDATE', c, function (c) {
                      if (returnTo) {
                        res.redirect(returnTo);
                      } else {
                        // use the reference to the originally id deifned by req.moduleParams.id
                        res.redirect('/s3/' + encodeURIComponent(id));
                      }
                      next();
                    });
                  });
                }
              });
            });
          });
        } else {
          req.flash('error', req.t('Could not locate asset, it may have been deleted by another user or there has been an error.'));
          res.redirect('/asset');
          next();
        }
      });

var id = req.moduleParams.id;

Content.findById(id, function (err, c) {
  if (c) {

    // Default mapper
    calipso.form.mapFields(form.content, c);

    // Fields that are mapped specifically
    c.updated = new Date();
    c.alias = form.content.alias ? form.content.alias : titleAlias(c.title);
    c.tags = form.content.tags ? form.content.tags.replace(/[\s]+/g, "").split(",") : [];

    // Get content type
    ContentType.findOne({contentType:form.content.contentType}, function (err, contentType) {

      if (err || !contentType) {
        req.flash('error', req.t('Could not save content as I was unable to locate content type {type}.', {type:form.content.contentType}));
        res.redirect('/content');
        next();
      } else {

        // Copy over content type data
        c.contentType = contentType.contentType;
        c.layout = contentType.layout;
        c.ispublic = contentType.ispublic;

        // Emit pre event
        // This does not allow you to change the content
        calipso.e.pre_emit('CONTENT_UPDATE', c, function (c) {

          c.save(function (err) {
            if (err) {

              var errorMsg = '';
              if (err.errors) {
                for (var error in err.errors) {
                  errorMessage = error + " " + err.errors[error] + '\r\n';
                }
              } else {
                errorMessage = err.message;
              }
              req.flash('error', req.t('Could not update content because {msg}', {msg:errorMessage}));
              if (res.statusCode != 302) {
                // Don't redirect if we already are, multiple errors
                res.redirect('back');
              }
              next();

            } else {

              req.flash('info', req.t('Content saved.'));

              // Raise CONTENT_CREATE event
              calipso.e.post_emit('CONTENT_UPDATE', c, function (c) {
                if (returnTo) {
                  res.redirect(returnTo);
                } else {
                  // use the reference to the originally id deifned by req.moduleParams.id
                  res.redirect('/content/show/' + encodeURIComponent(id));
                }
                next();
              });

            }

          });

        });

      }

    });

  } else {
    req.flash('error', req.t('Could not locate content, it may have been deleted by another user or there has been an error.'));
    res.redirect('/content');
    next();
  }
});

var alias = req.moduleParams.alias;

