      res.format = 'json';
      res.statusCode = 200;
      conf = conf.configuration;
      res.send(conf);
      next();
    });
  } else {
    fs.readFile(calipso.config.file, function (err, data) {
      if (err) return next(err);
      try {
        config = JSON.parse(data);
      }
      catch (e) {
        return next(e);
      }
      res.format = 'json';
      res.statusCode = 200;
      res.send(config);
      next();
    });
  }
}

