Content.findOne({alias:options.alias}, function (err, c) {

  if (err || !c) {

    var text;
    if (options.clickCreate) {
      text = "<a title='" + req.t("Click to create") + " ...' href='/content/new?" +
        "type=Block%20Content" +
        "&alias=" + options.alias +
